import React, { useState, useEffect } from 'react';

const ArticleDetailPage = ({ article, onClose }) => {
  const [isVisible, setIsVisible] = useState(false);
  useEffect(() => {
    setIsVisible(true);
    return () => setIsVisible(false);
  }, [article]);

  if (!article) return null;

  return (
    <div
      className={`fixed inset-0 bg-gray-900 bg-opacity-90 flex items-center justify-center p-4 z-50 transition-opacity duration-500 ${isVisible ? 'opacity-100' : 'opacity-0'}`}
    >
      <div className="bg-white rounded-xl shadow-2xl max-w-7xl w-full max-h-[90vh] overflow-y-auto transform transition-all duration-500 ${isVisible ? 'scale-100' : 'scale-95'}">
        <div className="relative">
          <img
            src={article.image || 'https://placehold.co/1200x600/CCCCCC/000000?text=No+Image'}
            alt={article.title}
            className="w-full h-64 md:h-96 object-cover rounded-t-xl"
          />
          <button
            onClick={onClose}
            className="absolute cursor-pointer top-4 right-4 bg-gray-800 text-white rounded-full p-2 shadow-lg hover:bg-gray-700 transition-colors duration-300"
            aria-label="Close"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>
        <div className="p-6 md:p-8">
          <h1 className="text-3xl md:text-4xl font-extrabold text-gray-900 mb-4 leading-tight">{article.title}</h1>
          <div className="flex flex-wrap items-center text-sm text-gray-600 mb-6 space-x-4">
            {article.author && <span>By <span className="font-semibold">{article.author}</span></span>}
            <span>{article.date}</span>
            <span>{article.time}</span>
          </div>
          <p className="text-base md:text-lg text-gray-800 leading-relaxed whitespace-pre-wrap">
            {article.fullContent || article.description || 'No full content available for this article.'}
          </p>
          <div className="mt-8 flex justify-end">
            <button
              onClick={onClose}
              className="bg-blue-600 cursor-pointer text-white px-6 py-3 rounded-lg shadow-md hover:bg-blue-700 transition-colors duration-300 text-lg font-medium"
            >
              Back to Headlines
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ArticleDetailPage;

import React from 'react';

const ChildHeadlineCard = ({ article, onClick }) => {
  return (
    <div
      className="w-full bg-green-500 rounded-2xl shadow-lg overflow-hidden cursor-pointer transform transition-all duration-300 ease-in-out hover:scale-105"
      onClick={() => onClick(article)}
    >
      <img
        src={article.image}
        alt={article.title}
        className="w-full h-40 object-cover rounded-t-2xl"
      />
      <div className="p-4 bg-white text-gray-800">
        <h4 className="text-lg font-semibold mb-1 cursor-pointer hover:underline" onClick={(e) => { e.stopPropagation(); onClick(article); }}>{article.title}</h4>
        <p className="text-sm text-gray-600 mb-2 truncate">{article.description}</p>
        <div className="text-xs text-gray-500 flex items-center justify-between">
          <span>{article.date} ‚Ä¢ {article.time}</span>
          <span>By {article.author}</span>
          <button className="text-gray-500 cursor-pointer hover:text-blue-600 ml-2">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.684 13.342C8.886 12.938 9 12.482 9 12c0-.482-.114-.938-.316-1.342m0 2.684a3 3 0 110-2.684m0 2.684l6.632 3.316m-6.632-6l6.632-3.316m0 0a3 3 0 105.367-2.684 3 3 0 00-5.367 2.684zm0 0a3 3 0 110 2.684 3 3 0 010-2.684zm0 0a3 3 0 105.367 2.684 3 3 0 00-5.367-2.684z" />
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChildHeadlineCard;

import React from 'react';

const DotIndicators = ({ count, activeIndex, onDotClick }) => {
  return (
    <div className="flex justify-center items-center space-x-2 absolute bottom-4 w-full z-30">
      {Array.from({ length: count }).map((_, index) => (
        <span
          key={index}
          className={`
            block rounded-full cursor-pointer transition-all duration-300 ease-in-out
            ${index === activeIndex ? 'w-6 h-2 bg-white' : 'w-2 h-2 bg-gray-400'}
          `}
          onClick={() => onDotClick(index)}
        ></span>
      ))}
    </div>
  );
};

export default DotIndicators;

import React from 'react';
import DotIndicators from './DotIndicators';

const MainHeadlineCard = ({ article, onClick, onPrev, onNext, totalHeadlines, currentHeadlineIndex, onDotClick }) => {
  return (
    <div className="relative w-full h-80 md:h-96 bg-yellow-400 rounded-2xl shadow-lg overflow-hidden group">
      <div className="absolute inset-0 z-10 cursor-pointer" onClick={() => onClick(article)}></div>
      <img
        src={article.image}
        alt={article.title}
        className="absolute inset-0 w-full h-full object-cover opacity-70 group-hover:opacity-100 transition-opacity duration-500"
      />
      <div className="absolute inset-0 bg-gradient-to-t from-gray-900 to-transparent p-6 flex flex-col justify-end text-white z-20">
        <h3
          onClick={(e) => { e.stopPropagation(); onClick(article); }}
          className="text-2xl md:text-3xl font-bold mb-2 leading-tight cursor-pointer hover:underline"
        >
          {article.title}
        </h3>
        <p className="text-base md:text-lg mb-4 opacity-90">{article.description}</p>
        <div className="flex items-center text-sm opacity-80">
          <span>{article.date}</span>
          <span className="mx-2">‚Ä¢</span>
          <span>{article.time}</span>
        </div>
      </div>
      <button
        onClick={(e) => { e.stopPropagation(); onPrev(); }}
        className="absolute left-4 top-8 transform -translate-y-1/2 bg-gray-800 bg-opacity-70 text-white p-2 rounded-full shadow-lg transition-opacity duration-300 z-30 hover:bg-gray-700"
        aria-label="Previous Headline"
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 md:h-6 md:w-6 cursor-pointer" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
        </svg>
      </button>
      <button
        onClick={(e) => { e.stopPropagation(); onNext(); }}
        className="absolute right-4 top-8 transform -translate-y-1/2 bg-gray-800 bg-opacity-70 text-white p-2 rounded-full shadow-lg transition-opacity duration-300 z-30 hover:bg-gray-700"
        aria-label="Next Headline"
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 md:h-6 md:w-6 cursor-pointer" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
        </svg>
      </button>
      <DotIndicators count={totalHeadlines} activeIndex={currentHeadlineIndex} onDotClick={onDotClick} />
    </div>
  );
};

export default MainHeadlineCard;

